mapper.update:
update book set book_id =#{bookDto.bookId}, book_title=#{bookDto.bookTitle}, book_author=#{bookDto.bookAuthor}, 
book_publication_date=#{bookDto.bookPublicationDate}, book_price=#{bookDto.bookPrice}, book_publisher =#{bookDto.bookPublisher},
book_page_count=#{bookDto.bookPageCount}, book_genre =#{bookDto.bookGenre} where book_id =#{bookId}
